import tweepyconsumer_key_path = "./.apikeys/consumer_key"consumer_secret_path = "./.apikeys/consumer_secret"access_token_path = "./.apikeys/access_token"access_token_secret_path = "./.apikeys/access_token_secret"with open(consumer_key_path,"r") as f:    consumer_key = f.read().splitlines()[0]with open(consumer_secret_path,"r") as f:    consumer_secret = f.read().splitlines()[0]with open(access_token_path,"r") as f:    access_token = f.read().splitlines()[0]with open(access_token_secret_path,"r") as f:    access_token_secret = f.read().splitlines()[0]auth = tweepy.OAuthHandler(consumer_key, consumer_secret)auth.set_access_token(access_token, access_token_secret)client = tweepy.Client(consumer_key = consumer_key, consumer_secret = consumer_secret,                        access_token = access_token, access_token_secret = access_token_secret)api = tweepy.API(auth = auth)test_jpg = "./TEST.JPG"media = api.media_upload(filename = test_jpg)media_id = media.media_idclient.create_tweet(text = "He tuiteado esta mierda gracias a la API de twitter ", media_ids = [media_id])