#import import tweepyclass twitter_handler:	def __init__(self,consumer_key_path, consumer_secret_path, access_token_path, access_token_secret_path):		#inicializar las claves y autenticar la api y el usuario		with open(consumer_key_path,"r") as f:			consumer_key = f.read().splitlines()[0]		with open(consumer_secret_path,"r") as f:			consumer_secret = f.read().splitlines()[0]		with open(access_token_path,"r") as f:			access_token = f.read().splitlines()[0]		with open(access_token_secret_path,"r") as f:			access_token_secret = f.read().splitlines()[0]		self.auth = tweepy.OAuthHandler(consumer_key, consumer_secret)		self.auth.set_access_token(access_token, access_token_secret)		self.client = tweepy.Client(consumer_key = consumer_key, consumer_secret = consumer_secret, 				access_token = access_token, access_token_secret = access_token_secret)		self.api = tweepy.API(auth = self.auth)		return	def sendTweet(self,tweet_text,media_path):		# funcion para enviar un tuit		if media_path!=None:			media = self.api.media_upload(filename = media_path)			media_id = media.media_id			self.client.create_tweet(text = tweet_text, media_ids = [media_id])		else:			self.client.create_tweet(text = tweet_text)